// Basic csgoproto module for CS2 demo parsing // This is a simplified version adapted from the original demoparser project
// Enum definitions for demo message types #[derive(Copy, Clone, Debug, PartialEq, Eq, Hash, PartialOrd, Ord)] #[repr(i32)] pub enum EDemoCommands { DEM_Error = 0, DEM_Stop = 1, DEM_FileHeader = 2, DEM_FileInfo = 3, DEM_SyncTick = 4, DEM_SendTables = 5, DEM_ClassInfo = 6, DEM_StringTables = 7, DEM_Packet = 8, DEM_SignonPacket = 9, DEM_ConsoleCmd = 10, DEM_CustomData = 11, DEM_CustomDataCallbacks = 12, DEM_UserCmd = 13, DEM_FullPacket = 14, DEM_MAX = 15, DEM_IsCompressed = 0x80, }
// Basic voice data structure needed by the parser #[derive(Clone, PartialEq, Debug)] pub struct CsvcMsgVoiceData { pub audio: Vec<u8>, pub client: i32, pub audible_mask: i64, pub proximity: bool, pub format: i32, pub sequence_bytes: i32, pub section_number: i32, pub uncompressed_sample_offset: i32, }
