#[cfg(test)]
mod tests {
    use crate::parser::{DemoParser, DemoResult};
    use std::path::Path;
    use std::fs::File;
    use std::io::Read;

    #[test]
    fn test_parser_minimal_demo() {
        // Create a parser instance
        let parser = DemoParser::new();

        // Read a test demo file from the test_data directory
        let demo_path = Path::new("test_data/test_demo.dem");
        let mut file = File::open(demo_path).expect("Failed to open test demo file");
        let mut demo_data = Vec::new();
        file.read_to_end(&mut demo_data).expect("Failed to read test demo file");

        // Parse the demo file
        let result = parser.parse(&demo_data);

        // Verify the parser produced a valid result
        assert!(result.is_ok(), "Parser failed to parse demo file");

        let demo_result = result.unwrap();

        // Verify the demo has ticks
        assert!(!demo_result.ticks().is_empty(), "Demo contains no ticks");

        // Verify the demo has players
        let first_tick = &demo_result.ticks()[0];
        let players: Vec<_> = first_tick.players().collect();
        assert!(!players.is_empty(), "Demo contains no players");

        // Verify we can extract some player info
        let player = &players[0];
        println!("Player SteamID: {}", player.steamid);
        println!("Player name: {}", player.name);
    }
}

// First pass parser module
// Adapted from https://github.com/LaihoE/demoparser

use anyhow::Result;
use std::collections::HashMap;
use std::io::Cursor;
use byteorder::{LittleEndian, ReadBytesExt};

/// Represents information about fullpacket positions in the demo file
#[derive(Clone)]
pub struct FullPacketInfo {
    pub offset: usize,
    pub tick: i32,
}

/// Output from the first pass of parsing
#[derive(Clone)]
pub struct FirstPassOutput {
    pub fullpacket_offsets: Vec<FullPacketInfo>,
    pub string_tables: HashMap<String, Vec<String>>,
    pub entity_mapping: HashMap<i32, String>,
}

/// Parser for the first pass
pub struct FirstPassParser {
    wanted_player_props: Vec<String>,
}

impl FirstPassParser {
    /// Create a new first pass parser
    pub fn new(wanted_player_props: Vec<String>) -> Self {
        FirstPassParser {
            wanted_player_props,
        }
    }

    /// Parse the demo file to identify key data structures
    pub fn parse_demo(&mut self, demo_bytes: &[u8]) -> Result<FirstPassOutput> {
        let mut cursor = Cursor::new(demo_bytes);

        // Skip the header (assuming the header has been read already)
        cursor.set_position(1072); // 8 + 4*4 + 260*4 + 4*4 bytes for a standard CS2 demo header

        // Find fullpacket positions - in a real implementation this would scan
        // the file for packet boundaries and record their positions
        let mut fullpacket_offsets = Vec::new();

        // This is a simplified implementation - in a real parser we would:
        // 1. Scan for command packets
        // 2. Record positions of fullpackets
        // 3. Process string tables
        // 4. Build entity mappings

        // For our mock implementation, we'll create some simulated fullpackets
        for i in 0..100 {
            fullpacket_offsets.push(FullPacketInfo {
                offset: 1072 + i * 1000, // Simulate offsets
                tick: i,
            });
        }

        // Create other mock data structures
        let mut string_tables = HashMap::new();
        string_tables.insert("playerinfo".to_string(), vec![
            "Player1".to_string(), 
            "Player2".to_string()
        ]);

        let mut entity_mapping = HashMap::new();
        entity_mapping.insert(0, "player".to_string());
        entity_mapping.insert(1, "weapon".to_string());

        Ok(FirstPassOutput {
            fullpacket_offsets,
            string_tables,
            entity_mapping,
        })
    }
}

