pub struct ParserInputs {
    pub wanted_player_props: Vec<String>,
    pub wanted_world_props: Vec<String>,
    pub skip_string_tables: bool,
    pub skipped_tables: Vec<String>,
    pub wanted_ticks: HashSet<i32>,
}

pub struct FirstPassParser<'a> {
    pub input: &'a ParserInputs,
    pub prop_controller: PropController,
}

impl<'a> FirstPassParser<'a> {
    pub fn new(input: &'a ParserInputs) -> Self {
        let prop_controller = PropController::new(&input.wanted_player_props);
        FirstPassParser {
            input,
            prop_controller,
        }
    }
}

