use ahash::AHashMap;
use crate::parser::second_pass::decoder::Decoder;
use std::fmt::{Debug, Formatter};

pub const PROP_ID: u32 = 1;
pub const STEAMID_ID: u32 = 2;
pub const TICK_ID: u32 = 3;
pub const NAME_ID: u32 = 4;

#[derive(Clone, Debug)]
pub struct PropInfo {
    pub prop_name: String,
    pub prop_id: u32,
    pub decoder: Option<Decoder>,
    pub norm_scale: Option<f32>,
    pub send_table_prop_name: Option<String>,
    pub entity_prop_name: String,
}

#[derive(Clone)]
pub struct PropController {
    pub wanted_player_props: Vec<String>,
    pub prop_infos: Vec<PropInfo>,
    pub id_to_idx: AHashMap<u32, usize>,
}

impl Debug for PropController {
    fn fmt(&self, f: &mut Formatter<'_>) -> std::fmt::Result {
        f.debug_struct("PropController")
            .field("wanted_player_props", &self.wanted_player_props)
            .field("prop_infos", &self.prop_infos)
            .finish()
    }
}

impl PropController {
    pub fn new(props: &[String]) -> Self {
        let wanted_player_props = props.to_vec();
        let mut prop_infos = Vec::new();
        let mut id_to_idx = AHashMap::new();

        // Add built-in properties
        let steamid = PropInfo {
            prop_name: "steamID".to_string(),
            prop_id: STEAMID_ID,
            decoder: None,
            norm_scale: None,
            send_table_prop_name: None,
            entity_prop_name: "steamID".to_string(),
        };
        prop_infos.push(steamid);
        id_to_idx.insert(STEAMID_ID, 0);

        let tick = PropInfo {
            prop_name: "tick".to_string(),
            prop_id: TICK_ID,
            decoder: None,
            norm_scale: None,
            send_table_prop_name: None,
            entity_prop_name: "tick".to_string(),
        };
        prop_infos.push(tick);
        id_to_idx.insert(TICK_ID, 1);

        let name = PropInfo {
            prop_name: "name".to_string(),
            prop_id: NAME_ID,
            decoder: None,
            norm_scale: None,
            send_table_prop_name: None,
            entity_prop_name: "name".to_string(),
        };
        prop_infos.push(name);
        id_to_idx.insert(NAME_ID, 2);

        PropController {
            wanted_player_props,
            prop_infos,
            id_to_idx,
        }
    }

    pub fn get_prop_info(&self, id: u32) -> Option<&PropInfo> {
        if let Some(idx) = self.id_to_idx.get(&id) {
            return Some(&self.prop_infos[*idx]);
        }
        None
    }

    pub fn get_idx_by_id(&self, id: u32) -> Option<usize> {
        self.id_to_idx.get(&id).copied()
    }

    pub fn add_prop(&mut self, prop_info: PropInfo) -> usize {
        let id = prop_info.prop_id;
        let idx = self.prop_infos.len();
        self.prop_infos.push(prop_info);
        self.id_to_idx.insert(id, idx);
        idx
    }
}

use bitter::{BitReader, LittleEndianReader};
use std::io::Cursor;
use thiserror::Error;

#[derive(Error, Debug)]
pub enum DemoParserError {
    #[error("could not find prototype name")]
    MissingPrototypeName,
    #[error("could not find class name")]
    MissingClassName,
    #[error("could not find classes in DEM_ClassInfo")]
    MissingClasses,
    #[error("could not find class id in DEM_ClassInfo")]
    MissingClassId,
    #[error("create reader bit buffer is too small")]
    BitReaderCreateError,
    #[error("failed to uncompress packet")]
    SnappyError,
    #[error("packet message error")]
    FailedToReadPacketMessage,
    #[error("malformed message")]
    MalformedMessage,
    #[error("unknown prop type")]
    UnknownPropType,
    #[error("unknown decoder")]
    UnknownDecoder,
    #[error("could not allocate memory")]
    CouldNotAllocateMemory,
    #[error("io error: {0}")]
    IoError(#[from] std::io::Error),
    #[error("protobuf error: {0}")]
    ProtobufError(#[from] prost::DecodeError),
}

pub fn create_reader<'a>(buf: &'a [u8], offset: usize, max_bits: usize) -> Result<LittleEndianReader, DemoParserError> {
    if offset > buf.len() || max_bits / 8 > buf.len() {
        return Err(DemoParserError::BitReaderCreateError);
    }
    let reader = LittleEndianReader::new(&buf[offset..]);
    Ok(reader)
}

/// Extract binary data from a u32 bitfield
pub fn extract_bit_field(bit_field: u32, bit_size: u32, bit_start: u32) -> u32 {
    (bit_field >> bit_start) & ((1 << bit_size) - 1)
}

pub fn read_var_int32(reader: &mut LittleEndianReader) -> i32 {
    let mut result: i32 = 0;
    let mut count = 0;
    let mut b: u8;

    loop {
        if count == 5 {
            return result;
        }
        b = reader.read_bits(8) as u8;
        result |= ((b & 0x7f) as i32) << (7 * count);
        count += 1;
        if (b & 0x80) == 0 {
            break;
        }
    }
    if (result >> 31) == 1 {
        result = ((result) << 1) >> 1;
        return -result;
    }
    result
}

pub fn read_var_u64(reader: &mut LittleEndianReader) -> u64 {
    let mut result: u64 = 0;
    let mut count = 0;
    let mut b: u8;

    loop {
        if count == 10 {
            return result;
        }
        b = reader.read_bits(8) as u8;
        result |= ((b & 0x7f) as u64) << (7 * count);
        count += 1;
        if (b & 0x80) == 0 {
            break;
        }
    }
    result
}

pub fn read_string(reader: &mut LittleEndianReader) -> String {
    let mut s = Vec::new();
    loop {
        let b = reader.read_bits(8) as u8;
        if b == 0 {
            break;
        }
        s.push(b);
    }
    String::from_utf8_lossy(&s).to_string()
}

