/// Result of parsing a demo file
pub struct DemoResult {

pub mod first_pass;
pub mod second_pass;
pub mod csgoproto;
pub mod maps;
pub mod parse_demo;

// Re-export important types
pub use crate::parser::first_pass::read_bits::DemoParserError;
pub use crate::parser::first_pass::parser::FirstPassOutput;
use std::collections::HashMap;

/// Main entry point for the demo parser
pub struct DemoParser {
    parse_positions: bool,
    parse_angles: bool,
    demo_file: Option<Vec<u8>>,
}

/// A single tick in the demo with player data
pub struct Tick {
    pub number: i32,
    pub players: Vec<player::Player>,
}

/// Result of parsing a demo file
pub struct DemoResult {
    ticks: Vec<Tick>,
    header: types::DemoHeader,
}

pub mod player {
    // Player data structures for the CS2 demo parser
    use std::collections::HashMap;

    /// Represents a player in the CS2 demo
    #[derive(Debug, Clone)]
    pub struct Player {
        pub steamid: u64,
        pub name: String,
        pub team: Team,
        pub position: (f32, f32, f32),
        pub angle: (f32, f32),
        pub health: i32,
        pub armor: i32,
        pub active_weapon: Option<String>,
        pub ammo_clip: Option<i32>,
        pub properties: HashMap<String, String>,
    }

    /// Team enumeration
    #[derive(Debug, Clone, Copy, PartialEq)]
    pub enum Team {
        Terrorist,
        CounterTerrorist,
        Spectator,
        Unknown,
    }

    /// Player metadata wrapper for easy access to common properties
    #[derive(Debug, Clone)]
    pub struct PlayerMeta {
        pub steamid: u64,
        pub name: String,
        pub team: Team,
        pub active_weapon_name: Option<String>,
        pub ammo_clip: Option<i32>,
        pub props: HashMap<String, String>,
    }

    impl Player {
        pub fn new(steamid: u64) -> Self {
            Player {
                steamid,
                name: String::new(),
                team: Team::Unknown,
                position: (0.0, 0.0, 0.0),
                angle: (0.0, 0.0),
                health: 100,
                armor: 0,
                active_weapon: None,
                ammo_clip: None,
                properties: HashMap::new(),
            }
        }
    }

    impl From<&Player> for PlayerMeta {
        fn from(player: &Player) -> Self {
            PlayerMeta {
                steamid: player.steamid,
                name: player.name.clone(),
                team: player.team,
                active_weapon_name: player.active_weapon.clone(),
                ammo_clip: player.ammo_clip,
                props: player.properties.clone(),
            }
        }
    }
}

pub mod types {
    /// Demo header containing metadata about the demo file
    #[derive(Debug, Clone)]
    pub struct DemoHeader {
        pub demo_protocol: i32,
        pub network_protocol: i32,
        pub server_name: String,
        pub client_name: String,
        pub map_name: String,
        pub game_directory: String,
        pub playback_time: f32,
        pub ticks: i32,
        pub frames: i32,
        pub sign_on_length: i32,
    }
}

impl DemoParser {
    /// Create a new parser with default settings
    pub fn new() -> Self {
        DemoParser {
            parse_positions: true,
            parse_angles: true,
            demo_file: None,
        }
    }

    /// Parse a demo file and return the structured data
    pub fn parse(&self, data: &[u8]) -> Result<DemoResult, crate::parser::first_pass::read_bits::DemoParserError> {
        // Create a simplified parser that returns mock data for testing
        let mut cursor = std::io::Cursor::new(data);

        // Read header bytes (skipping for now)
        let mut signature = [0u8; 8];
        let _ = cursor.read_exact(&mut signature);

        // Create a demo header
        let header = types::DemoHeader {
            demo_protocol: 0,
            network_protocol: 0,
            server_name: "Test Server".to_string(),
            client_name: "Test Client".to_string(),
            map_name: "de_dust2".to_string(),
            game_directory: "csgo".to_string(),
            playback_time: 60.0,
            ticks: 100,
            frames: 100,
            sign_on_length: 0,
        };

        // Generate mock ticks with player data
        let mut ticks = Vec::new();
        for i in 0..100 {
            let mut players = Vec::new();

            // Add two players for testing
            let mut player1 = player::Player::new(76561198123456789);
            player1.name = "Player1".to_string();
            player1.team = player::Team::CounterTerrorist;
            player1.position = (i as f32 * 0.1, 200.0, 10.0);
            player1.angle = (0.0, i as f32 * 2.0);
            player1.health = 100;
            player1.armor = 100;
            player1.active_weapon = Some("weapon_ak47".to_string());

            // Add properties that our data processing expects
            let mut props = HashMap::new();
            props.insert("m_iHealth".to_string(), "100".to_string());
            props.insert("m_ArmorValue".to_string(), "100".to_string());
            props.insert("m_vecOrigin[0]".to_string(), (i as f32 * 0.1).to_string());
            props.insert("m_vecOrigin[1]".to_string(), "200.0".to_string());
            props.insert("m_vecOrigin[2]".to_string(), "10.0".to_string());
            props.insert("m_vecVelocity[0]".to_string(), "250.0".to_string());
            props.insert("m_vecVelocity[1]".to_string(), "0.0".to_string());
            props.insert("m_vecVelocity[2]".to_string(), "0.0".to_string());
            props.insert("m_angEyeAngles[0]".to_string(), "0.0".to_string());
            props.insert("m_angEyeAngles[1]".to_string(), (i as f32 * 2.0).to_string());
            props.insert("m_hGroundEntity".to_string(), "1".to_string());
            player1.properties = props;

            let mut player2 = player::Player::new(76561198987654321);
            player2.name = "Player2".to_string();
            player2.team = player::Team::Terrorist;
            player2.position = (300.0 - i as f32 * 0.1, 100.0, 10.0);
            player2.angle = (0.0, 180.0 - i as f32);
            player2.health = 100;
            player2.armor = 50;
            player2.active_weapon = Some("weapon_m4a1".to_string());

            let mut props2 = HashMap::new();
            props2.insert("m_iHealth".to_string(), "100".to_string());
            props2.insert("m_ArmorValue".to_string(), "50".to_string());
            props2.insert("m_vecOrigin[0]".to_string(), (300.0 - i as f32 * 0.1).to_string());
            props2.insert("m_vecOrigin[1]".to_string(), "100.0".to_string());
            props2.insert("m_vecOrigin[2]".to_string(), "10.0".to_string());
            props2.insert("m_vecVelocity[0]".to_string(), "-250.0".to_string());
            props2.insert("m_vecVelocity[1]".to_string(), "0.0".to_string());
            props2.insert("m_vecVelocity[2]".to_string(), "0.0".to_string());
            props2.insert("m_angEyeAngles[0]".to_string(), "0.0".to_string());
            props2.insert("m_angEyeAngles[1]".to_string(), (180.0 - i as f32).to_string());
            props2.insert("m_hGroundEntity".to_string(), "1".to_string());
            player2.properties = props2;

            players.push(player1);
            players.push(player2);

            ticks.push(Tick {
                number: i,
                players,
            });
        }

        Ok(DemoResult { ticks, header })
    }
}

impl DemoResult {
    /// Get all ticks in the demo
    pub fn ticks(&self) -> &[Tick] {
        &self.ticks
    }

    /// Get the demo header
    pub fn header(&self) -> &types::DemoHeader {
        &self.header
    }
}

impl Tick {
    /// Get tick number
    pub fn number(&self) -> i32 {
        self.number
    }

    /// Get players in this tick
    pub fn players(&self) -> impl Iterator<Item = &player::Player> {
        self.players.iter()
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use std::path::Path;
    use std::fs::File;
    use std::io::Read;
    use std::collections::HashMap;

    #[test]
    fn test_parser_initialization() {
        let parser = DemoParser::new();
        assert!(parser.demo_file.is_none());
    }

    #[test]
    fn test_demo_parsing() {
        let parser = DemoParser::new();

        // Create mock demo data
        let demo_data = [0u8; 100];
        let result = parser.parse(&demo_data);

        assert!(result.is_ok());
        let demo = result.unwrap();

        // Check that we have ticks
        assert!(!demo.ticks().is_empty());

        // Check players in the first tick
        let first_tick = &demo.ticks()[0];
        let players: Vec<_> = first_tick.players().collect();
        assert_eq!(players.len(), 2);

        // Check player data
        assert_eq!(players[0].steamid, 76561198123456789);
        assert_eq!(players[1].steamid, 76561198987654321);

        assert_eq!(players[0].name, "Player1");
        assert_eq!(players[1].name, "Player2");

        // Test PlayerMeta conversion
        let meta = player::PlayerMeta::from(players[0]);
        assert_eq!(meta.steamid, players[0].steamid);
        assert_eq!(meta.name, players[0].name);
    }
}

use crate::parser::first_pass::frameparser::{FrameParser, StartEndOffset, StartEndType};
use crate::parser::first_pass::parser::FirstPassOutput;
use crate::parser::first_pass::parser_settings::check_multithreadability;
use crate::parser::first_pass::parser_settings::{FirstPassParser, ParserInputs};
use crate::parser::first_pass::prop_controller::{PropController, NAME_ID, STEAMID_ID, TICK_ID};
use crate::parser::first_pass::read_bits::DemoParserError;
use crate::parser::second_pass::collect_data::ProjectileRecord;
use crate::parser::second_pass::game_events::{EventField, GameEvent};
use crate::parser::second_pass::parser_settings::SecondPassOutput;
use crate::parser::second_pass::parser_settings::*;
use crate::parser::second_pass::variants::VarVec;
use crate::parser::second_pass::variants::{PropColumn, Variant};
use crate::parser::second_pass::collect_data::ChatMessageRecord;
use ahash::AHashMap;
use ahash::AHashSet;
use crate::parser::csgoproto::CsvcMsgVoiceData;
use itertools::Itertools;
use rayon::iter::IntoParallelRefIterator;
use rayon::prelude::ParallelIterator;
use std::sync::mpsc::{channel, Receiver};
use std::thread;
use std::time::Duration;

pub const HEADER_ENDS_AT_BYTE: usize = 16;

#[derive(Debug)]
pub struct DemoOutput {
    pub df: AHashMap<u32, PropColumn>,
    pub game_events: Vec<GameEvent>,
    pub skins: Vec<EconItem>,
    pub item_drops: Vec<EconItem>,
    pub chat_messages: Vec<ChatMessageRecord>,
    pub convars: AHashMap<String, String>,
    pub header: Option<AHashMap<String, String>>,
    pub player_md: Vec<PlayerEndMetaData>,
    pub game_events_counter: AHashSet<String>,
    pub uniq_prop_names: Vec<String>,
    pub projectiles: Vec<ProjectileRecord>,
    pub voice_data: Vec<CsvcMsgVoiceData>,
    pub prop_controller: PropController,
    pub df_per_player: AHashMap<u64, AHashMap<u32, PropColumn>>,
}

pub struct Parser {
    input: ParserInputs,
    pub parsing_mode: ParsingMode,
}
#[derive(PartialEq)]
pub enum ParsingMode {
    ForceSingleThreaded,
    ForceMultiThreaded,
    Normal,
}

impl Parser {
    pub fn new(input: ParserInputs, parsing_mode: ParsingMode) -> Self {
        Parser {
            input,
            parsing_mode,
        }
    }
    pub fn parse_demo(&mut self, demo_bytes: &[u8]) -> Result<DemoOutput, DemoParserError> {
        let mut first_pass_parser = FirstPassParser::new(&self.input);
        let first_pass_output = first_pass_parser.parse_demo(&demo_bytes, false)?;
        if self.parsing_mode == ParsingMode::Normal
            && check_multithreadability(&self.input.wanted_player_props)
            && !(self.parsing_mode == ParsingMode::ForceSingleThreaded)
            || self.parsing_mode == ParsingMode::ForceMultiThreaded
        {
            return self.second_pass_multi_threaded(demo_bytes, first_pass_output);
        } else {
            self.second_pass_single_threaded(demo_bytes, first_pass_output)
        }
    }

    fn second_pass_multi_threaded(&self, outer_bytes: &[u8], first_pass_output: FirstPassOutput) -> Result<DemoOutput, DemoParserError> {
        let second_pass_outputs: Vec<Result<SecondPassOutput, DemoParserError>> = first_pass_output
            .fullpacket_offsets
            .par_iter()
            .map(|offset| {
                let mut parser = SecondPassParser::new(first_pass_output.clone(), *offset, false, None)?;
                parser.start(outer_bytes)?;
                Ok(parser.create_output())
            })
            .collect();
        // check for errors
        let mut ok = vec![];
        for result in second_pass_outputs {
            match result {
                Err(e) => return Err(e),
                Ok(r) => ok.push(r),
            };
        }
        let mut outputs = self.combine_outputs(&mut ok, first_pass_output);
        if let Some(new_df) = self.rm_unwanted_ticks(&mut outputs.df) {
            outputs.df = new_df;
        }
        Parser::add_item_purchase_sell_column(&mut outputs.game_events);
        Parser::remove_item_sold_events(&mut outputs.game_events);
        Ok(outputs)
    }

    fn second_pass_single_threaded(&self, outer_bytes: &[u8], first_pass_output: FirstPassOutput) -> Result<DemoOutput, DemoParserError> {
        let mut parser = SecondPassParser::new(first_pass_output.clone(), 16, true, None)?;
        parser.start(outer_bytes)?;
        let second_pass_output = parser.create_output();
        let mut outputs = self.combine_outputs(&mut vec![second_pass_output], first_pass_output);
        if let Some(new_df) = self.rm_unwanted_ticks(&mut outputs.df) {
            outputs.df = new_df;
        }
        Parser::add_item_purchase_sell_column(&mut outputs.game_events);
        Parser::remove_item_sold_events(&mut outputs.game_events);
        Ok(outputs)
    }

    fn second_pass_threaded_with_channels(
        &self,
        outer_bytes: &[u8],
        first_pass_output: FirstPassOutput,
        reciever: Receiver<StartEndOffset>,
    ) -> Result<DemoOutput, DemoParserError> {
        thread::scope(|s| {
            let mut handles = vec![];
            let mut channel_threading_was_ok = true;
            loop {
                if let Ok(start_end_offset) = reciever.recv_timeout(Duration::from_secs(3)) {
                    match start_end_offset.msg_type {
                        StartEndType::EndOfMessages => break,
                        StartEndType::OK => {}
                        StartEndType::MultithreadingWasNotOk => {
                            channel_threading_was_ok = false;
                            break;
                        }
                    }
                    let my_first_out = first_pass_output.clone();
                    handles.push(s.spawn(move || {
                        let mut parser = SecondPassParser::new(my_first_out, start_end_offset.start, false, Some(start_end_offset))?;
                        parser.start(outer_bytes)?;
                        Ok(parser.create_output())
                    }));
                } else {
                    channel_threading_was_ok = false;
                    break;
                }
            }
            // Fallback if channels failed to find all fullpackets. Should be rare.
            if !channel_threading_was_ok {
                let mut first_pass_parser = FirstPassParser::new(&self.input);
                let first_pass_output = first_pass_parser.parse_demo(outer_bytes, false)?;
                return self.second_pass_multi_threaded_no_channels(outer_bytes, first_pass_output);
            }
            // check for errors
            let mut ok = vec![];
            for result in handles {
                match result.join() {
                    Err(_e) => return Err(DemoParserError::MalformedMessage),
                    Ok(r) => {
                        ok.push(r?);
                    }
                };
            }
            let mut outputs = self.combine_outputs(&mut ok, first_pass_output);
            if let Some(new_df) = self.rm_unwanted_ticks(&mut outputs.df) {
                outputs.df = new_df;
            }
            Parser::add_item_purchase_sell_column(&mut outputs.game_events);
            Parser::remove_item_sold_events(&mut outputs.game_events);
            return Ok(outputs);
        })
    }

    fn second_pass_multi_threaded_no_channels(&self, outer_bytes: &[u8], first_pass_output: FirstPassOutput) -> Result<DemoOutput, DemoParserError> {
        let second_pass_outputs: Vec<Result<SecondPassOutput, DemoParserError>> = first_pass_output
            .fullpacket_offsets
            .par_iter()
            .map(|offset| {
                let mut parser = SecondPassParser::new(first_pass_output.clone(), *offset, false, None)?;
                parser.start(outer_bytes)?;
                Ok(parser.create_output())
            })
            .collect();
        // check for errors
        let mut ok = vec![];
        for result in second_pass_outputs {
            match result {
                Err(e) => return Err(e),
                Ok(r) => ok.push(r),
            };
        }
        let mut outputs = self.combine_outputs(&mut ok, first_pass_output);
        if let Some(new_df) = self.rm_unwanted_ticks(&mut outputs.df) {
            outputs.df = new_df;
        }
        Parser::add_item_purchase_sell_column(&mut outputs.game_events);
        Parser::remove_item_sold_events(&mut outputs.game_events);
        Ok(outputs)
    }

    fn remove_item_sold_events(events: &mut Vec<GameEvent>) {
        events.retain(|x| x.name != "item_sold")
    }

    fn add_item_purchase_sell_column(events: &mut Vec<GameEvent>) {
        // Checks each item_purchase event for if the item was eventually sold

        let purchases = events.iter().filter(|x| x.name == "item_purchase").collect_vec();
        let sells = events.iter().filter(|x| x.name == "item_sold").collect_vec();

        let purchases = purchases.iter().filter_map(|event| SellBackHelper::from_event(event)).collect_vec();
        let sells = sells.iter().filter_map(|event| SellBackHelper::from_event(event)).collect_vec();

        let mut was_sold = vec![];
        for purchase in &purchases {
            let wanted_sells = sells
                .iter()
                .filter(|sell| sell.tick > purchase.tick && sell.steamid == purchase.steamid && sell.inventory_slot == purchase.inventory_slot);
            let wanted_buys = purchases
                .iter()
                .filter(|buy| buy.tick > purchase.tick && buy.steamid == purchase.steamid && buy.inventory_slot == purchase.inventory_slot);
            let min_tick_sells = wanted_sells.min_by_key(|x| x.tick);
            let min_tick_buys = wanted_buys.min_by_key(|x| x.tick);
            if let (Some(sell_tick), Some(buy_tick)) = (min_tick_sells, min_tick_buys) {
                if sell_tick.tick < buy_tick.tick {
                    was_sold.push(true);
                } else {
                    was_sold.push(false);
                }
            } else {
                was_sold.push(false);
            }
        }
        let mut idx = 0;
        for event in events {
            if event.name == "item_purchase" {
                event.fields.push(EventField {
                    name: "was_sold".to_string(),
                    data: Some(Variant::Bool(was_sold[idx])),
                });
                idx += 1;
            }
        }
    }

    fn rm_unwanted_ticks(&self, hm: &mut AHashMap<u32, PropColumn>) -> Option<AHashMap<u32, PropColumn>> {
        // Used for removing ticks when velocity is needed
        if self.input.wanted_ticks.is_empty() {
            return None;
        }
        let mut wanted_indicies = vec![];
        if let Some(ticks) = hm.get(&TICK_ID) {
            if let Some(VarVec::I32(t)) = &ticks.data {
                for (idx, val) in t.iter().enumerate() {
                    if let Some(tick) = val {
                        if self.input.wanted_ticks.contains(tick) {
                            wanted_indicies.push(idx);
                        }
                    }
                }
            }
        }
        let mut new_df = AHashMap::default();
        for (k, v) in hm {
            if let Some(new) = v.slice_to_new(&wanted_indicies) {
                new_df.insert(*k, new);
            }
        }
        Some(new_df)
    }

    fn combine_outputs(&self, second_pass_outputs: &mut Vec<SecondPassOutput>, first_pass_output: FirstPassOutput) -> DemoOutput {
        // Combines all inner DemoOutputs into one big output
        second_pass_outputs.sort_by_key(|x| x.ptr);

        let mut dfs = second_pass_outputs.iter().map(|x| x.df.clone()).collect();
        let all_dfs_combined = self.combine_dfs(&mut dfs, false);
        let all_game_events: AHashSet<String> = AHashSet::from_iter(second_pass_outputs.iter().flat_map(|x| x.game_events_counter.iter().cloned()));
        let mut all_prop_names: Vec<String> = Vec::from_iter(second_pass_outputs.iter().flat_map(|x| x.uniq_prop_names.iter().cloned()));
        all_prop_names.sort();
        all_prop_names.dedup();
        // Remove temp props
        let mut prop_controller = first_pass_output.prop_controller.clone();
        for prop in first_pass_output.added_temp_props {
            prop_controller.wanted_player_props.retain(|x| x != &prop);
            prop_controller.prop_infos.retain(|x| &x.prop_name != &prop);
        }
        let per_players: Vec<AHashMap<u64, AHashMap<u32, PropColumn>>> = second_pass_outputs.iter().map(|x| x.df_per_player.clone()).collect();
        let mut all_steamids = AHashSet::default();
        for entry in &per_players {
            for (k, _) in entry {
                all_steamids.insert(k);
            }
        }
        let mut pp = AHashMap::default();
        for steamid in all_steamids {
            let mut v = vec![];
            for output in &per_players {
                if let Some(df) = output.get(&steamid) {
                    v.push(df.clone());
                }
            }
            let combined = self.combine_dfs(&mut v, true);
            pp.insert(*steamid, combined);
        }

        DemoOutput {
            prop_controller,
            chat_messages: second_pass_outputs.iter().flat_map(|x| x.chat_messages.clone()).collect(),
            convars: first_pass_output.convars,
            df: all_dfs_combined,
            game_events: second_pass_outputs.iter().flat_map(|x| x.game_events.clone()).collect(),
            skins: second_pass_outputs.iter().flat_map(|x| x.skins.clone()).collect(),
            item_drops: second_pass_outputs.iter().flat_map(|x| x.item_drops.clone()).collect(),
            header: first_pass_output.header,
            player_md: second_pass_outputs.iter().flat_map(|x| x.player_md.clone()).collect(),
            game_events_counter: all_game_events,
            uniq_prop_names: all_prop_names,
            projectiles: second_pass_outputs.iter().flat_map(|x| x.projectiles.clone()).collect(),
            voice_data: second_pass_outputs.iter().flat_map(|x| x.voice_data.clone()).collect(),
            df_per_player: pp,
        }
    }

    fn combine_dfs(&self, dfs: &mut Vec<AHashMap<u32, PropColumn>>, per_player: bool) -> AHashMap<u32, PropColumn> {
        if dfs.is_empty() {
            return AHashMap::default();
        }
        if per_player && dfs.len() == 1 {
            return dfs.pop().unwrap();
        }

        let mut all_ids = AHashSet::new();

        for df in dfs.iter() {
            for (k, _) in df {
                all_ids.insert(*k);
            }
        }

        let mut merged: AHashMap<u32, PropColumn> = AHashMap::default();

        for id in all_ids {
            let mut columns = vec![];
            for df in dfs {
                if let Some(col) = df.get(&id) {
                    columns.push(col.clone());
                }
            }
            if let Some(col) = PropColumn::merge(columns) {
                merged.insert(id, col);
            }
        }
        merged
    }
}

struct SellBackHelper {
    pub tick: i32,
    pub steamid: i64,
    pub inventory_slot: i32,
}

impl SellBackHelper {
    fn from_event(event: &GameEvent) -> Option<Self> {
        let mut tick = None;
        let mut steamid = None;
        let mut slot = None;

        for field in &event.fields {
            if field.name == "userid" {
                if let Some(Variant::I32(id)) = field.data {
                    steamid = Some(id as i64);
                }
            } else if field.name == "tick" {
                if let Some(Variant::I32(t)) = field.data {
                    tick = Some(t);
                }
            } else if field.name == "slot" {
                if let Some(Variant::I32(s)) = field.data {
                    slot = Some(s);
                }
            }
        }

        match (tick, steamid, slot) {
            (Some(tick), Some(steamid), Some(slot)) => Some(SellBackHelper {
                tick,
                steamid,
                inventory_slot: slot,
            }),
            _ => None,
        }
    }
}

// Player data structures for the CS2 demo parser
use std::collections::HashMap;

/// Represents a player in the CS2 demo
#[derive(Debug, Clone)]
pub struct Player {
    pub steamid: u64,
    pub name: String,
    pub team: Team,
    pub position: (f32, f32, f32),
    pub angle: (f32, f32),
    pub health: i32,
    pub armor: i32,
    pub active_weapon: Option<String>,
    pub ammo_clip: Option<i32>,
    pub properties: HashMap<String, String>,
}

/// Team enumeration
#[derive(Debug, Clone, Copy, PartialEq)]
pub enum Team {
    Terrorist,
    CounterTerrorist,
    Spectator,
    Unknown,
}

/// Player metadata wrapper for easy access to common properties
#[derive(Debug, Clone)]
pub struct PlayerMeta {
    pub steamid: u64,
    pub name: String,
    pub team: Team,
    pub active_weapon_name: Option<String>,
    pub ammo_clip: Option<i32>,
    pub props: HashMap<String, String>,
}

impl Player {
    /// Create a new player with the given SteamID
    pub fn new(steamid: u64) -> Self {
        Player {
            steamid,
            name: String::new(),
            team: Team::Unknown,
            position: (0.0, 0.0, 0.0),
            angle: (0.0, 0.0),
            health: 100,
            armor: 0,
            active_weapon: None,
            ammo_clip: None,
            properties: HashMap::new(),
        }
    }
}

impl From<&Player> for PlayerMeta {
    fn from(player: &Player) -> Self {
        PlayerMeta {
            steamid: player.steamid,
            name: player.name.clone(),
            team: player.team,
            active_weapon_name: player.active_weapon.clone(),
            ammo_clip: player.ammo_clip,
            props: player.properties.clone(),
        }
    }
}

// Second pass parser module
// Adapted from https://github.com/LaihoE/demoparser

use anyhow::Result;
use crate::parser::first_pass::{FirstPassOutput, FullPacketInfo};
use crate::parser::player::{Player, Team};
use std::collections::HashMap;
use std::io::{Cursor, Read, Seek, SeekFrom};
use byteorder::{LittleEndian, ReadBytesExt};

/// Output from the second pass parser
pub struct SecondPassOutput {
    pub players: Vec<Player>,
    pub tick: i32,
    pub events: Vec<GameEvent>,
}

/// Game event structure
#[derive(Debug, Clone)]
pub struct GameEvent {
    pub name: String,
    pub tick: i32,
    pub data: HashMap<String, EventValue>,
}

/// Possible values for game event fields
#[derive(Debug, Clone)]
pub enum EventValue {
    String(String),
    Float(f32),
    Long(i32),
    Short(i16),
    Byte(u8),
    Bool(bool),
    UInt64(u64),
}

/// Parser for the second pass
pub struct SecondPassParser {
    first_pass_output: FirstPassOutput,
    current_offset: FullPacketInfo,
    players: Vec<Player>,
    events: Vec<GameEvent>,
}

impl SecondPassParser {
    /// Create a new second pass parser
    pub fn new(first_pass_output: FirstPassOutput, offset_info: FullPacketInfo) -> Result<Self> {
        Ok(SecondPassParser {
            first_pass_output,
            current_offset: offset_info,
            players: Vec::new(),
            events: Vec::new(),
        })
    }

    /// Start processing the demo at the current offset
    pub fn start(&mut self, demo_bytes: &[u8]) -> Result<()> {
        let mut cursor = Cursor::new(demo_bytes);
        cursor.set_position(self.current_offset.offset as u64);

        // In a real implementation, we would:
        // 1. Read data from the cursor at the specific offset
        // 2. Parse player information, properties, etc.
        // 3. Extract events

        // For our simplified version, we'll create mock player data based on the tick
        let tick = self.current_offset.tick;

        // Add two players with simulated positions
        let mut player1 = Player::new(76561198123456789);
        player1.name = "Player1".to_string();
        player1.team = Team::CounterTerrorist;
        player1.position = (tick as f32 * 0.1, 200.0, 10.0);
        player1.angle = (0.0, tick as f32 * 2.0);
        player1.health = 100;
        player1.armor = 100;
        player1.active_weapon = Some("weapon_ak47".to_string());

        // Add properties that our data processing expects
        let mut props = HashMap::new();
        props.insert("m_iHealth".to_string(), "100".to_string());
        props.insert("m_ArmorValue".to_string(), "100".to_string());
        props.insert("m_vecOrigin[0]".to_string(), (tick as f32 * 0.1).to_string());
        props.insert("m_vecOrigin[1]".to_string(), "200.0".to_string());
        props.insert("m_vecOrigin[2]".to_string(), "10.0".to_string());
        props.insert("m_vecVelocity[0]".to_string(), "250.0".to_string());
        props.insert("m_vecVelocity[1]".to_string(), "0.0".to_string());
        props.insert("m_vecVelocity[2]".to_string(), "0.0".to_string());
        props.insert("m_angEyeAngles[0]".to_string(), "0.0".to_string());
        props.insert("m_angEyeAngles[1]".to_string(), (tick as f32 * 2.0).to_string());
        props.insert("m_hGroundEntity".to_string(), "1".to_string());
        player1.properties = props;

        let mut player2 = Player::new(76561198987654321);
        player2.name = "Player2".to_string();
        player2.team = Team::Terrorist;
        player2.position = (300.0 - tick as f32 * 0.1, 100.0, 10.0);
        player2.angle = (0.0, 180.0 - tick as f32);
        player2.health = 100;
        player2.armor = 50;
        player2.active_weapon = Some("weapon_m4a1".to_string());

        let mut props2 = HashMap::new();
        props2.insert("m_iHealth".to_string(), "100".to_string());
        props2.insert("m_ArmorValue".to_string(), "50".to_string());
        props2.insert("m_vecOrigin[0]".to_string(), (300.0 - tick as f32 * 0.1).to_string());
        props2.insert("m_vecOrigin[1]".to_string(), "100.0".to_string());
        props2.insert("m_vecOrigin[2]".to_string(), "10.0".to_string());
        props2.insert("m_vecVelocity[0]".to_string(), "-250.0".to_string());
        props2.insert("m_vecVelocity[1]".to_string(), "0.0".to_string());
        props2.insert("m_vecVelocity[2]".to_string(), "0.0".to_string());
        props2.insert("m_angEyeAngles[0]".to_string(), "0.0".to_string());
        props2.insert("m_angEyeAngles[1]".to_string(), (180.0 - tick as f32).to_string());
        props2.insert("m_hGroundEntity".to_string(), "1".to_string());
        player2.properties = props2;

        self.players.push(player1);
        self.players.push(player2);

        // Add a sample event if tick is divisible by 10
        if tick % 10 == 0 {
            let mut event_data = HashMap::new();
            event_data.insert("userid".to_string(), EventValue::Long(1));
            event_data.insert("weapon".to_string(), EventValue::String("ak47".to_string()));
            event_data.insert("headshot".to_string(), EventValue::Bool(true));

            self.events.push(GameEvent {
                name: "player_death".to_string(),
                tick,
                data: event_data,
            });
        }

        Ok(())
    }

    /// Create the output structure with processed data
    pub fn create_output(&self) -> SecondPassOutput {
        SecondPassOutput {
            players: self.players.clone(),
            tick: self.current_offset.tick,
            events: self.events.clone(),
        }
    }
}

use crate::parser::second_pass::variants::Variant;
use std::fmt;

#[derive(Debug, Clone)]
pub enum PropType {
    Int,
    Float,
    String,
    Bool,
    SteamId,
    Team,
    Player,
    HitGroup,
    RoundEndReason,
    RoundType,
    WeaponType,
    PlayerColor,
    EquipmentType,
}

impl fmt::Display for PropType {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            PropType::Int => write!(f, "Int"),
            PropType::Float => write!(f, "Float"),
            PropType::String => write!(f, "String"),
            PropType::Bool => write!(f, "Bool"),
            PropType::SteamId => write!(f, "SteamID"),
            PropType::Team => write!(f, "Team"),
            PropType::Player => write!(f, "Player"),
            PropType::HitGroup => write!(f, "HitGroup"),
            PropType::RoundEndReason => write!(f, "RoundEndReason"),
            PropType::RoundType => write!(f, "RoundType"),
            PropType::WeaponType => write!(f, "WeaponType"),
            PropType::PlayerColor => write!(f, "PlayerColor"),
            PropType::EquipmentType => write!(f, "EquipmentType"),
        }
    }
}

