    pub fn new(tick: i32, grenade_id: i32, thrower_steamid: i64, projectile_type: String, pos_x: f32, pos_y: f32, pos_z: f32) -> Self {
        Self {
            tick,
            grenade_id,
            thrower_steamid,
            projectile_type,
            pos_x,
            pos_y,
            pos_z,
        }
    }
}

