// Entity management module for CS2 demo parser
// This handles tracking of game entities and their properties

use crate::parser::second_pass::variants::Variant;
use ahash::AHashMap;

#[derive(Debug, Clone)]
pub struct Entity {
    pub entity_id: i32,
    pub class_id: i32,
    pub serial_num: i32,
    pub properties: AHashMap<String, Variant>,
}

impl Entity {
    pub fn new(entity_id: i32, class_id: i32, serial_num: i32) -> Self {
        Entity {
            entity_id,
            class_id,
            serial_num,
            properties: AHashMap::default(),
        }
    }

    pub fn update_property(&mut self, prop_name: &str, value: Variant) {
        self.properties.insert(prop_name.to_string(), value);
    }

    pub fn get_property(&self, prop_name: &str) -> Option<&Variant> {
        self.properties.get(prop_name)
    }
}

#[derive(Debug, Clone)]
pub struct EntityManager {
    pub entities: AHashMap<i32, Entity>,
    pub class_mapping: AHashMap<i32, String>,
}

impl EntityManager {
    pub fn new() -> Self {
        EntityManager {
            entities: AHashMap::default(),
            class_mapping: AHashMap::default(),
        }
    }

    pub fn add_entity(&mut self, entity: Entity) {
        self.entities.insert(entity.entity_id, entity);
    }

    pub fn get_entity(&self, entity_id: i32) -> Option<&Entity> {
        self.entities.get(&entity_id)
    }

    pub fn get_entity_mut(&mut self, entity_id: i32) -> Option<&mut Entity> {
        self.entities.get_mut(&entity_id)
    }

    pub fn remove_entity(&mut self, entity_id: i32) -> Option<Entity> {
        self.entities.remove(&entity_id)
    }

    pub fn get_entity_class(&self, entity_id: i32) -> Option<&String> {
        if let Some(entity) = self.get_entity(entity_id) {
            return self.class_mapping.get(&entity.class_id);
        }
        None
    }

    pub fn update_class_mapping(&mut self, class_id: i32, class_name: String) {
        self.class_mapping.insert(class_id, class_name);
    }
}

use crate::parser::second_pass::variants::Variant;
use std::fmt;

#[derive(Debug, Clone)]
pub struct GameEvent {
    pub name: String,
    pub tick: i32,
    pub fields: Vec<EventField>,
}

#[derive(Debug, Clone)]
pub struct EventField {
    pub name: String,
    pub data: Option<Variant>,
}

impl GameEvent {
    pub fn new(name: String, tick: i32) -> Self {
        GameEvent {
            name,
            tick,
            fields: Vec::new(),
        }
    }

    pub fn add_field(&mut self, name: String, data: Option<Variant>) {
        self.fields.push(EventField { name, data });
    }

    pub fn get_field(&self, name: &str) -> Option<&EventField> {
        self.fields.iter().find(|f| f.name == name)
    }

    pub fn get_field_value(&self, name: &str) -> Option<&Variant> {
        self.get_field(name).and_then(|f| f.data.as_ref())
    }
}

impl fmt::Display for GameEvent {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        write!(f, "GameEvent {{ name: {}, tick: {}, fields: [", self.name, self.tick)?;
        for (i, field) in self.fields.iter().enumerate() {
            if i > 0 {
                write!(f, ", ")?;
            }
            write!(f, "{}: {:?}", field.name, field.data)?;
        }
        write!(f, "] }}")
    }
}

