use crate::parser::first_pass::parser::FirstPassOutput; use crate::parser::first_pass::frameparser::{StartEndOffset, StartEndType}; use crate::parser::second_pass::collect_data::{ChatMessageRecord, ProjectileRecord}; use crate::parser::second_pass::game_events::GameEvent; use crate::parser::second_pass::variants::{PropColumn, Variant}; use ahash::AHashMap; use ahash::AHashSet;
#[derive(Clone)] pub struct EconItem { pub item_id: i64, pub account_id: i64, pub inventory: i32, pub item_def: i32, pub quality: i32, pub style: i32, pub paint_seed: i32, pub paint_wear: f32, pub paint_kit: i32, pub origin: i32, pub custom_name: String, pub stickers: Vec<Sticker>, }
#[derive(Clone)] pub struct Sticker { pub slot: i32, pub sticker_id: i32, pub wear: f32, pub scale: f32, pub rotation: f32, pub tint_id: i32, }
#[derive(Clone)] pub struct PlayerEndMetaData { pub steamid: i64, pub name: String, pub user_id: i32, pub kills: i32, pub deaths: i32, pub assists: i32, pub score: i32, pub mvps: i32, pub team: i32, }
/// Handles the second pass of parsing pub struct SecondPassParser { pub first_pass_output: FirstPassOutput, pub ptr: usize, pub is_single_threaded: bool, pub start_end: Option<StartEndOffset>,
// Collection of parsed data pub df: AHashMap<u32, PropColumn>, pub game_events: Vec<GameEvent>, pub skins: Vec<EconItem>, pub item_drops: Vec<EconItem>, pub chat_messages: Vec<ChatMessageRecord>, pub player_md: Vec<PlayerEndMetaData>, pub game_events_counter: AHashSet<String>, pub uniq_prop_names: Vec<String>, pub projectiles: Vec<ProjectileRecord>, pub voice_data: Vec<crate::parser::csgoproto::CsvcMsgVoiceData>, pub df_per_player: AHashMap<u64, AHashMap<u32, PropColumn>>, }
impl SecondPassParser { pub fn new( first_pass_output: FirstPassOutput, offset: usize, is_single_threaded: bool, start_end: Option<StartEndOffset>, ) -> Result<Self, crate::parser::first_pass::read_bits::DemoParserError> { Ok(SecondPassParser { first_pass_output, ptr: offset, is_single_threaded, start_end, df: AHashMap::default(), game_events: Vec::new(), skins: Vec::new(), item_drops: Vec::new(), chat_messages: Vec::new(), player_md: Vec::new(), game_events_counter: AHashSet::default(), uniq_prop_names: Vec::new(), projectiles: Vec::new(), voice_data: Vec::new(), df_per_player: AHashMap::default(), }) }
pub fn start(&mut self, _demo_bytes: &[u8]) -> Result<(), crate::parser::first_pass::read_bits::DemoParserError> { // In a real implementation, this would process the demo data starting at self.ptr // For this simplified implementation, we'll just return success Ok(()) }
pub fn create_output(&self) -> SecondPassOutput { SecondPassOutput { ptr: self.ptr, df: self.df.clone(), game_events: self.game_events.clone(), skins: self.skins.clone(), item_drops: self.item_drops.clone(), chat_messages: self.chat_messages.clone(), player_md: self.player_md.clone(), game_events_counter: self.game_events_counter.clone(), uniq_prop_names: self.uniq_prop_names.clone(), projectiles: self.projectiles.clone(), voice_data: self.voice_data.clone(), df_per_player: self.df_per_player.clone(), } } }
/// Output from the second pass of parsing pub struct SecondPassOutput { pub ptr: usize, pub df: AHashMap<u32, PropColumn>, pub game_events: Vec<GameEvent>, pub skins: Vec<EconItem>, pub item_drops: Vec<EconItem>, pub chat_messages: Vec<ChatMessageRecord>, pub player_md: Vec<PlayerEndMetaData>, pub game_events_counter: AHashSet<String>, pub uniq_prop_names: Vec<String>, pub projectiles: Vec<ProjectileRecord>, pub voice_data: Vec<crate::parser::csgoproto::CsvcMsgVoiceData>, pub df_per_player: AHashMap<u64, AHashMap<u32, PropColumn>>, }

// Utility functions for path operations
// These are used for property path manipulation

/// Split a property path into components
pub fn split_prop_path(path: &str) -> Vec<String> {
    path.split('.')
        .map(|s| s.to_string())
        .collect()
}

/// Join property path components into a single path
pub fn join_prop_path(components: &[String]) -> String {
    components.join(".")
}

/// Get the parent path from a full property path
pub fn get_parent_path(path: &str) -> String {
    let components = split_prop_path(path);
    if components.len() <= 1 {
        return String::new();
    }
    join_prop_path(&components[0..components.len() - 1])
}

/// Get the leaf name from a full property path
pub fn get_leaf_name(path: &str) -> String {
    let components = split_prop_path(path);
    if components.is_empty() {
        return String::new();
    }
    components.last().unwrap().clone()
}

use ahash::AHashMap;
use std::fmt::{Debug, Display};

#[derive(Clone, Debug)]
pub enum Variant {
    Bool(bool),
    I32(i32),
    U32(u32),
    I64(i64),
    U64(u64),
    F32(f32),
    String(String),
    Vector(Vec<f32>),
}

impl Display for Variant {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            Variant::Bool(b) => write!(f, "{}", b),
            Variant::I32(i) => write!(f, "{}", i),
            Variant::U32(i) => write!(f, "{}", i),
            Variant::I64(i) => write!(f, "{}", i),
            Variant::U64(i) => write!(f, "{}", i),
            Variant::F32(fl) => write!(f, "{}", fl),
            Variant::String(s) => write!(f, "{}", s),
            Variant::Vector(v) => write!(f, "{:?}", v),
        }
    }
}

#[derive(Clone, Debug)]
pub enum VarVec {
    Bool(Vec<Option<bool>>),
    I32(Vec<Option<i32>>),
    U32(Vec<Option<u32>>),
    I64(Vec<Option<i64>>),
    U64(Vec<Option<u64>>),
    F32(Vec<Option<f32>>),
    String(Vec<Option<String>>),
    Vector(Vec<Option<Vec<f32>>>),
}

#[derive(Clone)]
pub struct PropColumn {
    pub prop_id: u32,
    pub data: Option<VarVec>,
}

impl Debug for PropColumn {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match &self.data {
            Some(data) => write!(f, "PropColumn {{ prop_id: {}, data: {:?} }}", self.prop_id, data),
            None => write!(f, "PropColumn {{ prop_id: {}, data: None }}", self.prop_id),
        }
    }
}

impl PropColumn {
    pub fn new(id: u32) -> Self {
        PropColumn {
            prop_id: id,
            data: None,
        }
    }

    pub fn push_val(&mut self, val: Option<Variant>) {
        if self.data.is_none() {
            self.init_data(&val);
        }

        match &mut self.data {
            Some(VarVec::Bool(vec)) => {
                if let Some(Variant::Bool(b)) = val {
                    vec.push(Some(b));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::I32(vec)) => {
                if let Some(Variant::I32(i)) = val {
                    vec.push(Some(i));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::U32(vec)) => {
                if let Some(Variant::U32(i)) = val {
                    vec.push(Some(i));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::I64(vec)) => {
                if let Some(Variant::I64(i)) = val {
                    vec.push(Some(i));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::U64(vec)) => {
                if let Some(Variant::U64(i)) = val {
                    vec.push(Some(i));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::F32(vec)) => {
                if let Some(Variant::F32(f)) = val {
                    vec.push(Some(f));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::String(vec)) => {
                if let Some(Variant::String(s)) = val {
                    vec.push(Some(s));
                } else {
                    vec.push(None);
                }
            }
            Some(VarVec::Vector(vec)) => {
                if let Some(Variant::Vector(v)) = val {
                    vec.push(Some(v));
                } else {
                    vec.push(None);
                }
            }
            None => {}
        }
    }

    fn init_data(&mut self, val: &Option<Variant>) {
        match val {
            Some(Variant::Bool(_)) => self.data = Some(VarVec::Bool(Vec::new())),
            Some(Variant::I32(_)) => self.data = Some(VarVec::I32(Vec::new())),
            Some(Variant::U32(_)) => self.data = Some(VarVec::U32(Vec::new())),
            Some(Variant::I64(_)) => self.data = Some(VarVec::I64(Vec::new())),
            Some(Variant::U64(_)) => self.data = Some(VarVec::U64(Vec::new())),
            Some(Variant::F32(_)) => self.data = Some(VarVec::F32(Vec::new())),
            Some(Variant::String(_)) => self.data = Some(VarVec::String(Vec::new())),
            Some(Variant::Vector(_)) => self.data = Some(VarVec::Vector(Vec::new())),
            None => {}
        }
    }

    pub fn slice_to_new(&self, indices: &[usize]) -> Option<PropColumn> {
        let mut result = PropColumn::new(self.prop_id);

        match &self.data {
            Some(VarVec::Bool(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::Bool(new_vec));
            }
            Some(VarVec::I32(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::I32(new_vec));
            }
            Some(VarVec::U32(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::U32(new_vec));
            }
            Some(VarVec::I64(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::I64(new_vec));
            }
            Some(VarVec::U64(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::U64(new_vec));
            }
            Some(VarVec::F32(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::F32(new_vec));
            }
            Some(VarVec::String(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::String(new_vec));
            }
            Some(VarVec::Vector(vec)) => {
                let mut new_vec = Vec::with_capacity(indices.len());
                for &idx in indices {
                    new_vec.push(vec.get(idx).cloned().flatten());
                }
                result.data = Some(VarVec::Vector(new_vec));
            }
            None => return None,
        }

        Some(result)
    }

    pub fn merge(columns: Vec<PropColumn>) -> Option<PropColumn> {
        if columns.is_empty() {
            return None;
        }

        if columns.len() == 1 {
            return Some(columns[0].clone());
        }

        let prop_id = columns[0].prop_id;
        let mut result = PropColumn::new(prop_id);

        // Initialize result data based on first column's type
        if let Some(data) = &columns[0].data {
            match data {
                VarVec::Bool(_) => result.data = Some(VarVec::Bool(Vec::new())),
                VarVec::I32(_) => result.data = Some(VarVec::I32(Vec::new())),
                VarVec::U32(_) => result.data = Some(VarVec::U32(Vec::new())),
                VarVec::I64(_) => result.data = Some(VarVec::I64(Vec::new())),
                VarVec::U64(_) => result.data = Some(VarVec::U64(Vec::new())),
                VarVec::F32(_) => result.data = Some(VarVec::F32(Vec::new())),
                VarVec::String(_) => result.data = Some(VarVec::String(Vec::new())),
                VarVec::Vector(_) => result.data = Some(VarVec::Vector(Vec::new())),
            }
        } else {
            return None;
        }

        // Concatenate all columns
        for col in columns {
            if let Some(data) = &col.data {
                match (data, &mut result.data) {
                    (VarVec::Bool(src), Some(VarVec::Bool(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::I32(src), Some(VarVec::I32(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::U32(src), Some(VarVec::U32(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::I64(src), Some(VarVec::I64(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::U64(src), Some(VarVec::U64(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::F32(src), Some(VarVec::F32(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::String(src), Some(VarVec::String(dst))) => dst.extend(src.iter().cloned()),
                    (VarVec::Vector(src), Some(VarVec::Vector(dst))) => dst.extend(src.iter().cloned()),
                    _ => return None, // Type mismatch
                }
            }
        }

        Some(result)
    }
}

