#[cfg(test)]
mod integration_tests {
    use crate::data::vectors_from_demo;
    use crate::data::write_to_parquet;
    use crate::parser::DemoParser;
    use crate::parser::player::PlayerMeta;
    use cs2_common::BehavioralVector;
    use std::path::Path;
    use tempfile::tempdir;
    use parquet::file::reader::{FileReader, SerializedFileReader};
    use std::fs::File;

    #[test]
    fn test_end_to_end_pipeline() {
        // Parse a demo file
        let demo_path = Path::new("test_data/test_demo.dem");
        let vectors = vectors_from_demo(demo_path).unwrap_or_else(|_| {
            // If the actual demo file parsing fails, use mock vectors for testing
            let mut vectors = Vec::new();
            for i in 0..10 {
                vectors.push(BehavioralVector {
                    tick: i,
                    steamid: 76561198123456789,
                    health: 100.0,
                    armor: 50.0,
                    pos_x: i as f32 * 0.1,
                    pos_y: 200.0,
                    pos_z: 10.0,
                    vel_x: 250.0,
                    vel_y: 0.0,
                    vel_z: 0.0,
                    yaw: i as f32 * 2.0,
                    pitch: 0.0,
                    weapon_id: 7,
                    ammo: 30.0,
                    is_airborne: 0.0,
                    delta_yaw: 2.0,
                    delta_pitch: 0.0,
                });
            }
            vectors
        });

        // Write vectors to parquet
        let tmp_dir = tempdir().unwrap();
        let parquet_path = tmp_dir.path().join("test_output.parquet");
        write_to_parquet(&vectors, &parquet_path).unwrap();

        // Verify the parquet file
        let reader = SerializedFileReader::new(File::open(&parquet_path).unwrap()).unwrap();

        // Check that the reader has data
        let metadata = reader.metadata();
        assert!(metadata.num_rows() > 0);

        // Read the rows back
        let mut row_iter = reader.get_row_iter(None).unwrap();

        // Verify the first row matches our first vector
        if let Some(row) = row_iter.next() {
            assert_eq!(row.get_int(0).unwrap(), vectors[0].tick as i64);
            assert_eq!(row.get_long(1).unwrap(), vectors[0].steamid as i64);
            assert_eq!(row.get_float(2).unwrap(), vectors[0].health);
            assert_eq!(row.get_float(3).unwrap(), vectors[0].armor);
            assert_eq!(row.get_float(4).unwrap(), vectors[0].pos_x);
            assert_eq!(row.get_float(5).unwrap(), vectors[0].pos_y);
        }
    }

    #[test]
    fn test_parser_with_real_data() {
        let parser = DemoParser::new();
        let demo_data = include_bytes!("../../../test_data/test_demo.dem");
        let result = parser.parse(demo_data).unwrap();

        // Verify the header
        let header = result.header();
        assert!(header.demo_protocol >= 0);
        assert!(header.network_protocol >= 0);

        // Verify ticks
        let ticks = result.ticks();
        assert!(!ticks.is_empty());

        // Process one tick window to simulate behavioral vectors
        if ticks.len() >= 2 {
            let current = &ticks[0];
            let next = &ticks[1];

            // For each player, create a behavioral vector
            let mut vectors = Vec::new();

            for (cur_p, next_p) in current.players().zip(next.players()) {
                let cur_meta = PlayerMeta::from(cur_p);
                let next_meta = PlayerMeta::from(next_p);

                // Verify that we can access player properties
                assert!(cur_meta.props.contains_key("m_iHealth"));
                assert!(cur_meta.props.contains_key("m_vecOrigin[0]"));

                // Create a behavioral vector from this player
                let health = cur_meta.props.get("m_iHealth")
                    .and_then(|v| v.parse::<f32>().ok())
                    .unwrap_or(0.0);

                let pos_x = cur_meta.props.get("m_vecOrigin[0]")
                    .and_then(|v| v.parse::<f32>().ok())
                    .unwrap_or(0.0);

                // Calculate delta yaw
                let cur_yaw = cur_meta.props.get("m_angEyeAngles[1]")
                    .and_then(|v| v.parse::<f32>().ok())
                    .unwrap_or(0.0);
                let next_yaw = next_meta.props.get("m_angEyeAngles[1]")
                    .and_then(|v| v.parse::<f32>().ok())
                    .unwrap_or(0.0);
                let delta_yaw = next_yaw - cur_yaw;

                // Verify values
                assert!(health >= 0.0);
                assert!(pos_x >= 0.0);
                assert!(delta_yaw.is_finite());
            }
        }
    }
}

// Type definitions for the CS2 demo parser
use std::collections::HashMap;

/// Demo header containing metadata about the demo file
#[derive(Debug, Clone)]
pub struct DemoHeader {
    pub demo_protocol: i32,
    pub network_protocol: i32,
    pub server_name: String,
    pub client_name: String,
    pub map_name: String,
    pub game_directory: String,
    pub playback_time: f32,
    pub ticks: i32,
    pub frames: i32,
    pub sign_on_length: i32,
}

/// Game event data structure
#[derive(Debug, Clone)]
pub struct GameEvent {
    pub name: String,
    pub tick: i32,
    pub data: HashMap<String, EventValue>,
}

/// Possible values in a game event
#[derive(Debug, Clone)]
pub enum EventValue {
    String(String),
    Float(f32),
    Long(i32),
    Short(i16),
    Byte(u8),
    Bool(bool),
    UInt64(u64),
}

use std::collections::HashMap;

#[derive(Debug, Clone)]
pub struct PlayerMeta {
    pub steamid: u64,
    pub props: HashMap<String, String>,
    pub active_weapon_name: Option<String>,
    pub ammo_clip: Option<u32>,
}

// Implement a conversion from a demo player to PlayerMeta
impl<T> From<&T> for PlayerMeta 
where
    T: PlayerLike,
{
    fn from(player: &T) -> Self {
        PlayerMeta {
            steamid: player.get_steamid(),
            props: player.get_props(),
            active_weapon_name: player.get_active_weapon_name(),
            ammo_clip: player.get_ammo_clip(),
        }
    }
}

// Trait to abstract over different player implementations
pub trait PlayerLike {
    fn get_steamid(&self) -> u64;
    fn get_props(&self) -> HashMap<String, String>;
    fn get_active_weapon_name(&self) -> Option<String>;
    fn get_ammo_clip(&self) -> Option<u32>;
}

use std::net::TcpListener;
use std::io::{Read, Write};
use tch::{nn, Device};
use cs2_common::InputVector;
use anyhow::Result;
use std::sync::Arc;
    let mut vs = nn::VarStore::new(Device::Cpu);
    vs.load(model_path)?;
    let net = crate::model::BehaviorNet::new(&vs.root(), 14, 2);
pub fn serve(model_path: &str, port: u16) -> Result<()> {
    // Temporarily disabled PyTorch model loading
    let net = BehaviorNet::new(14, 2); // Fix: Remove the third argument
    serve_with_model(net, port)
}

// Separated for testing
pub fn serve_with_model(net: crate::model::BehaviorNet, port: u16) -> Result<()> {
    let listener = TcpListener::bind(format!("0.0.0.0:{}", port))?;
    println!("Policy server listening on port {}", port);
    for stream in listener.incoming() {
        let mut stream = stream?;
        let mut buf = [0u8; std::mem::size_of::<InputVector>()];
        match stream.read_exact(&mut buf) {
            Ok(_) => {
                let input_vec: &InputVector = bytemuck::from_bytes(&buf);
                // Temporarily use placeholder prediction
                let output = net.predict(input_vec);
                if let Err(e) = stream.write_all(out_bytes) {
                    eprintln!("Error writing response: {}", e);
                }
            },
            Err(e) => {
                eprintln!("Error reading from client: {}", e);
            }
        }
    }
    Ok(())
}

// Modified serve function that checks shutdown flag
pub fn serve_with_model_with_shutdown(
    net: crate::model::BehaviorNet,
    port: u16,
    shutdown: Arc<AtomicBool>,
) -> Result<()> {
    let listener = TcpListener::bind(format!("0.0.0.0:{}", port))?;
    listener.set_nonblocking(true)?;
    println!("Policy server listening on port {}", port);

    while !shutdown.load(Ordering::SeqCst) {
        match listener.accept() {
            Ok((mut stream, _)) => {
                // Handle client
                let mut buf = [0u8; std::mem::size_of::<InputVector>()];
                match stream.read_exact(&mut buf) {
                    Ok(_) => {
                        let input_vec: &InputVector = bytemuck::from_bytes(&buf);
                        let output = net.predict(input_vec);
                        let out_bytes = bytemuck::bytes_of(&output);
                        if let Err(e) = stream.write_all(out_bytes) {
                            eprintln!("Error writing response: {}", e);
                        }
                    },
                    Err(e) => {
                        eprintln!("Error reading from client: {}", e);
                    }
                }
            },
            Err(e) if e.kind() == std::io::ErrorKind::WouldBlock => {
                // No connections available, sleep briefly
                std::thread::sleep(std::time::Duration::from_millis(10));
            },
            Err(e) => {
                eprintln!("Error accepting connection: {}", e);
                break;
            }
        }
    }

    Ok(())
}

#[cfg(test)]
mod tests {
    use super::*;
    use std::net::TcpStream;
    use std::thread;
    use std::time::Duration;
    use std::sync::{Arc, atomic::{AtomicBool, Ordering}};
    use cs2_common::OutputVector;

    struct TestServer {
        port: u16,
        handle: Option<thread::JoinHandle<()>>,
        shutdown: Arc<AtomicBool>,
    use std::net::SocketAddr;
    use tch::nn::VarStore;
    }

    impl TestServer {
        fn start() -> Self {
            let listener = TcpListener::bind("127.0.0.1:0").unwrap();
            let addr = listener.local_addr().unwrap();
            let port = addr.port();
            drop(listener);

            let shutdown = Arc::new(AtomicBool::new(false));
            let shutdown_clone = shutdown.clone();

            // Create a simple model
            let model = crate::model::BehaviorNet::new(14, 2);

            let handle = thread::spawn(move || {
                // Run server in a separate thread until shutdown
                let server_result = serve_with_model_with_shutdown(model, port, shutdown_clone);
                if let Err(e) = server_result {
                    eprintln!("Server error: {}", e);
            let vs = VarStore::new(Device::Cpu);
            let model = crate::model::BehaviorNet::new(&vs.root(), 14, 2);
            });

            // Give server time to start
            thread::sleep(Duration::from_millis(100));

            Self {
                port,
                handle: Some(handle),
                shutdown,
            }
        }
    }

    impl Drop for TestServer {
        fn drop(&mut self) {
            self.shutdown.store(true, Ordering::SeqCst);
            if let Some(handle) = self.handle.take() {
                let _ = handle.join();
            }
        }
    }

    // Modified serve function that checks shutdown flag
    fn serve_with_model_with_shutdown(
        net: crate::model::BehaviorNet,
        port: u16,
        shutdown: Arc<AtomicBool>,
    ) -> Result<()> {
        let listener = TcpListener::bind(format!("127.0.0.1:{}", port))?;
        listener.set_nonblocking(true)?;

        while !shutdown.load(Ordering::SeqCst) {
            match listener.accept() {
                Ok((mut stream, _)) => {
                    stream.set_nonblocking(false)?;

                    let mut buf = [0u8; std::mem::size_of::<InputVector>()];
                    match stream.read_exact(&mut buf) {
                        Ok(_) => {
                            let input_vec: &InputVector = bytemuck::from_bytes(&buf);
                            // Temporarily use placeholder prediction
                            let output = cs2_common::OutputVector {
                                delta_yaw: 0.0,
                                delta_pitch: 0.0
                            };
                            let out_bytes = bytemuck::bytes_of(&output);
                            let _ = stream.write_all(out_bytes);
                        },
                            let output = net.predict(input_vec);
                    thread::sleep(Duration::from_millis(10));
                },
                Err(_) => break,
            }
        }

        Ok(())
    }

    #[test]
    fn test_server_client_communication() {
        let server = TestServer::start();

        // Connect to server
        let addr = format!("127.0.0.1:{}", server.port);
        let mut stream = TcpStream::connect(addr).unwrap();

        // Send input vector
        let input = InputVector {
            health: 100.0,
            armor: 50.0,
            pos_x: 1.0,
            pos_y: 2.0,
            pos_z: 3.0,
            vel_x: 0.1,
            vel_y: 0.2,
            vel_z: 0.3,
            yaw: 90.0,
            pitch: 45.0,
            weapon_id_f32: 42.0,
            ammo: 30.0,
            is_airborne: 0.0,
            padding: 0.0,
        };
        let input_bytes = bytemuck::bytes_of(&input);
        stream.write_all(input_bytes).unwrap();

        // Read response
        let mut output_bytes = [0u8; std::mem::size_of::<OutputVector>()];
        stream.read_exact(&mut output_bytes).unwrap();

        // Parse output
        let output: OutputVector = *bytemuck::from_bytes(&output_bytes);

        // Verify output is valid (not checking exact values since the model is random)
        assert!(output.delta_yaw.is_finite());
        assert!(output.delta_pitch.is_finite());
    }
}

use std::net::TcpListener;
use std::io::{Read, Write};
use std::sync::Arc;
use std::sync::atomic::{AtomicBool, Ordering};
use anyhow::Result;
use candle_core::Device;
use cs2_common::InputVector;

use crate::model::BehaviorNet;

pub fn serve(port: u16) -> Result<()> {
    let net = BehaviorNet::new(12, 2, Device::Cpu)?; // Fixed: Added Device and proper error handling
    serve_with_model(net, port)
}

// Separated for testing
pub fn serve_with_model(_net: crate::model::BehaviorNet, port: u16) -> Result<()> {
    let listener = TcpListener::bind(format!("0.0.0.0:{}", port))?;
    println!("Policy server listening on port {}", port);
    for stream in listener.incoming() {
        let mut stream = stream?;
        let mut buf = [0u8; std::mem::size_of::<InputVector>()];
        match stream.read_exact(&mut buf) {
            Ok(_) => {
                let _input_vec: &InputVector = bytemuck::from_bytes(&buf);
                // Temporarily use placeholder prediction
                let output = cs2_common::OutputVector {
                    delta_yaw: 0.0,
                    delta_pitch: 0.0
                };
                let out_bytes = bytemuck::bytes_of(&output);
                if let Err(e) = stream.write_all(out_bytes) {
                    eprintln!("Error writing response: {}", e);
                }
            },
            Err(e) => {
                eprintln!("Error reading from client: {}", e);
            }
        }
    }
    Ok(())
}

// Modified serve function that checks shutdown flag
pub fn serve_with_model_with_shutdown(
    _net: crate::model::BehaviorNet,
    port: u16,
    shutdown: Arc<AtomicBool>,
) -> Result<()> {
    let listener = TcpListener::bind(format!("0.0.0.0:{}", port))?;
    listener.set_nonblocking(true)?;
    println!("Policy server listening on port {}", port);

    while !shutdown.load(Ordering::SeqCst) {
        match listener.accept() {
            Ok((mut stream, _)) => {
                // Handle client
                let mut buf = [0u8; std::mem::size_of::<InputVector>()];
                match stream.read_exact(&mut buf) {
                    Ok(_) => {
                        let _input_vec: &InputVector = bytemuck::from_bytes(&buf);
                        // Temporarily use placeholder prediction
                        let output = cs2_common::OutputVector {
                            delta_yaw: 0.0,
                            delta_pitch: 0.0
                        };
                        let out_bytes = bytemuck::bytes_of(&output);
                        if let Err(e) = stream.write_all(out_bytes) {
                            eprintln!("Error writing response: {}", e);
                        }
                    },
                    Err(e) => {
                        eprintln!("Error reading from client: {}", e);
                    }
                }
            },
            Err(e) if e.kind() == std::io::ErrorKind::WouldBlock => {
                // No connections available, sleep briefly
                std::thread::sleep(std::time::Duration::from_millis(10));
            },
            Err(e) => {
                eprintln!("Error accepting connection: {}", e);
                break;
            }
        }
    }

    Ok(())
}

#[cfg(test)]
mod tests {
    use super::*;
    use std::net::TcpStream;
    use std::thread;
    use std::time::Duration;
    use std::sync::{Arc, atomic::{AtomicBool, Ordering}};
    use tempfile::NamedTempFile;
    use cs2_common::OutputVector;

    struct TestServer {
        port: u16,
        handle: Option<thread::JoinHandle<()>>,
        shutdown: Arc<AtomicBool>,
    }

    impl TestServer {
        fn start() -> Self {
            // Find an available port
            let listener = TcpListener::bind("127.0.0.1:0").unwrap();
            let addr = listener.local_addr().unwrap();
            let port = addr.port();
            drop(listener);

            let shutdown = Arc::new(AtomicBool::new(false));
            let shutdown_clone = shutdown.clone();

            // Create a simple model
            let model = crate::model::BehaviorNet::new(14, 2);

            let handle = thread::spawn(move || {
                // Run server in a separate thread until shutdown
                let server_result = serve_with_model_with_shutdown(model, port, shutdown_clone);
                if let Err(e) = server_result {
                    eprintln!("Server error: {}", e);
                }
            });

            // Give server time to start
            thread::sleep(Duration::from_millis(100));

            Self {
                port,
                handle: Some(handle),
                shutdown,
            }
        }
    }

    impl Drop for TestServer {
        fn drop(&mut self) {
            self.shutdown.store(true, Ordering::SeqCst);
            if let Some(handle) = self.handle.take() {
                let _ = handle.join();
            }
        }
    }

    // Modified serve function that checks shutdown flag
    fn serve_with_model_with_shutdown(
        net: crate::model::BehaviorNet,
        port: u16,
        shutdown: Arc<AtomicBool>,
    ) -> Result<()> {
        let listener = TcpListener::bind(format!("127.0.0.1:{}", port))?;
        listener.set_nonblocking(true)?;

        while !shutdown.load(Ordering::SeqCst) {
            match listener.accept() {
                Ok((mut stream, _)) => {
                    stream.set_nonblocking(false)?;

                    let mut buf = [0u8; std::mem::size_of::<InputVector>()];
                    match stream.read_exact(&mut buf) {
                        Ok(_) => {
                            let input_vec: &InputVector = bytemuck::from_bytes(&buf);
                            // Temporarily use placeholder prediction
                            let output = cs2_common::OutputVector {
                                delta_yaw: 0.0,
                                delta_pitch: 0.0
                            };
                            let out_bytes = bytemuck::bytes_of(&output);
                            let _ = stream.write_all(out_bytes);
                        },
                        Err(_) => continue,
                    }
                },
                Err(e) if e.kind() == std::io::ErrorKind::WouldBlock => {
                    // No connections available, sleep a bit
                    thread::sleep(Duration::from_millis(10));
                },
                Err(_) => break,
            }
        }

        Ok(())
    }

    #[test]
    fn test_server_client_communication() {
        let server = TestServer::start();

        // Connect to server
        let addr = format!("127.0.0.1:{}", server.port);
        let mut stream = TcpStream::connect(addr).unwrap();

        // Send input vector
        let input = InputVector {
            health: 100.0,
            armor: 50.0,
            pos_x: 1.0,
            pos_y: 2.0,
            pos_z: 3.0,
            vel_x: 0.1,
            vel_y: 0.2,
            vel_z: 0.3,
            yaw: 90.0,
            pitch: 45.0,
            weapon_id_f32: 42.0,
            ammo: 30.0,
            is_airborne: 0.0,
            padding: 0.0,
        };
        let input_bytes = bytemuck::bytes_of(&input);
        stream.write_all(input_bytes).unwrap();

        // Read response
        let mut output_bytes = [0u8; std::mem::size_of::<OutputVector>()];
        stream.read_exact(&mut output_bytes).unwrap();

        // Parse output
        let output: OutputVector = *bytemuck::from_bytes(&output_bytes);

        // Verify output is valid (not checking exact values since the model is random)
        assert!(output.delta_yaw.is_finite());
        assert!(output.delta_pitch.is_finite());
    }
}
